import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterModule } from '@angular/router';
import { ProductService } from '../_service/product.service';
import { Product } from '../_model/product';

@Component({
  selector: 'app-product-list',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './product-list.component.html'
})
export class ProductListComponent implements OnInit {
  products: Product[] = [];
  // Modal de confirmación
  showConfirm = false;
  toDelete?: Product;

  constructor(
    private productService: ProductService,
    private router: Router
  ){}

  ngOnInit(): void {
    this.load();
  }

  load(): void {
    this.productService.list().subscribe({
      next: (data: Product[]) => {
        this.products = data;
      },
      error: (err: any) => {
        console.error('Error al cargar productos:', err);
        alert('Error al cargar los productos. Por favor, intente nuevamente.');
      }
    });
  }

  edit(p: Product): void {
    this.router.navigate(['/product', p.idProduct]);
  }

  confirmDelete(p: Product): void {
    this.toDelete = p;
    this.showConfirm = true;
  }

  cancelDelete(): void {
    this.showConfirm = false;
    this.toDelete = undefined;
  }

  deleteConfirmed(): void {
    if (!this.toDelete?.idProduct) { this.cancelDelete(); return; }
    this.productService.delete(this.toDelete.idProduct!).subscribe({
      next: () => {
        this.cancelDelete();
        this.load();
      },
      error: (err: any) => {
        console.error('Error al eliminar producto:', err);
        alert('Error al eliminar el producto. Por favor, intente nuevamente.');
      }
    });
  }
}
<h2>Productos</h2>
<button routerLink="/product/new">Nuevo Producto</button>
<table>
  <thead><tr><th>Nombre</th><th>Categoria</th><th>Familia</th><th>Laboratorio</th><th>Acciones</th></tr></thead>
  <tbody>
    <tr *ngFor="let p of products">
      <td>{{p.name}}</td>
      <td>{{p.category?.name || 'N/A'}}</td>
      <td>{{p.family?.name || 'N/A'}}</td>
      <td>{{p.laboratory?.name || 'N/A'}}</td>
      <td>
        <button class="edit-btn" (click)="edit(p)">Editar</button>
        <button class="delete-btn" (click)="confirmDelete(p)">Eliminar</button>
      </td>
    </tr>
  </tbody>
</table>

<!-- Modal de confirmación -->
<div class="modal-backdrop" *ngIf="showConfirm"></div>
<div class="modal" *ngIf="showConfirm">
  <div class="modal-content">
    <h3>Confirmar eliminación</h3>
    <p>¿Desea eliminar "{{ toDelete?.name }}"?</p>
  <p *ngIf="toDelete?.category?.name"><strong>Categoría:</strong> {{ toDelete?.category?.name }}</p>
  <p *ngIf="toDelete?.family?.name"><strong>Familia:</strong> {{ toDelete?.family?.name }}</p>
  <p *ngIf="toDelete?.laboratory?.name"><strong>Laboratorio:</strong> {{ toDelete?.laboratory?.name }}</p>
    <div class="modal-actions">
      <button class="delete-btn" (click)="deleteConfirmed()">Eliminar</button>
      <button type="button" (click)="cancelDelete()">Cancelar</button>
    </div>
  </div>

</div>
